import React, {useEffect, useState} from 'react';
import {Form} from 'semantic-ui-react'

function Search() {
  const [result, setResult] = useState("")

  useEffect(() => {
    fetch("https://api.github.com/users/rivera1294/repos")
      .then(res => res.json())
      .then(data => {
        const languages = data.map(language => (
          language.language
        ))
        favLanguage(languages)
      });
  }, [])

  const setData = result => {
    setResult(result)
  }

  const favLanguage = (lang) =>{
    const languages = {}, mostFreq = 0, which = [];

    lang.forEach(ea => {
      if (!obj[ea]) {
        languages[ea] = 1;
      } else {
        languages[ea]++;
      }

      if (languages[ea] > mostFreq) {
        mostFreq = languages[ea];
        which = [ea];
      } else if (languages[ea] === mostFreq) {
        which.push(ea);
      }
    });

    setData(which)
  }

  return (
    <div>
      <Form>
        <Form.Group>
          <Form.Input placeholder="Search for a github username" name="username" />
          <Form.Button content="Search" />
        </Form.Group>
      </Form>
      <div>
        {result}
      </div>
    </div>
  );
}


export default Search;
